import{_ as e,c as t,o as a,a as r}from"./app.9fb06215.js";const g='{"title":"Monorepo \u9879\u76EE\u7F16\u8BD1","description":"","frontmatter":{"category":"Study"},"headers":[{"level":3,"title":"\u96C6\u6210\u6253\u5305\u5DE5\u5177","slug":"\u96C6\u6210\u6253\u5305\u5DE5\u5177"},{"level":3,"title":"Rollup \u5F00\u53D1\u6846\u67B6 / \u5E93\u7684\u4F18\u52BF","slug":"rollup-\u5F00\u53D1\u6846\u67B6-\u5E93\u7684\u4F18\u52BF"},{"level":3,"title":"Rollup \u5F00\u53D1\u5E94\u7528\u7A0B\u5E8F\u7684\u7F3A\u70B9","slug":"rollup-\u5F00\u53D1\u5E94\u7528\u7A0B\u5E8F\u7684\u7F3A\u70B9"},{"level":2,"title":"\u57FA\u7840\u7F16\u8BD1\u5DE5\u5177","slug":"\u57FA\u7840\u7F16\u8BD1\u5DE5\u5177"},{"level":2,"title":"\u4E24\u79CD\u7C7B\u578B\u7684\u4F5C\u7528","slug":"\u4E24\u79CD\u7C7B\u578B\u7684\u4F5C\u7528"},{"level":2,"title":"TSC \u74F6\u9888 (TSC Bottleneck)","slug":"tsc-\u74F6\u9888-tsc-bottleneck"},{"level":3,"title":"Deno 1.0 \u6B63\u5F0F\u53D1\u5E03\u8BF4\u660E\u4E2D\u63D0\u5230","slug":"deno-1-0-\u6B63\u5F0F\u53D1\u5E03\u8BF4\u660E\u4E2D\u63D0\u5230"},{"level":3,"title":"Deno \u5185\u90E8\u90E8\u5206\u6A21\u5757\u505C\u7528 TypeScript","slug":"deno-\u5185\u90E8\u90E8\u5206\u6A21\u5757\u505C\u7528-typescript"},{"level":3,"title":"swc \u56E2\u961F\u6B63\u5728\u901A\u8FC7\u79FB\u690D tsc \u5230 Go","slug":"swc-\u56E2\u961F\u6B63\u5728\u901A\u8FC7\u79FB\u690D-tsc-\u5230-go"}],"relativePath":"docs__study/monorepo-compiler.md","lastUpdated":1668565559000}',o={name:"docs__study/monorepo-compiler.md"},l=r('<h1 id="monorepo-\u9879\u76EE\u7F16\u8BD1" tabindex="-1">Monorepo \u9879\u76EE\u7F16\u8BD1 <a class="header-anchor" href="#monorepo-\u9879\u76EE\u7F16\u8BD1" aria-hidden="true">#</a></h1><p>\u5728\u524D\u7AEF\u5F00\u53D1\u5F53\u4E2D\uFF0C\u6211\u4EEC\u4F1A\u7528\u5F88\u591A\u7F16\u8BD1\u5DE5\u5177\uFF1A<code>typescript compiler</code>\u3001<code>babel</code>\u3001<code>eslint</code>\u3001<code>postcss</code></p><p>Monorepo \u4E2D\uFF0C\u6211\u4EEC\u8FD8\u9700\u8981\u601D\u8003\u5E94\u8BE5\u9700\u8981\u54EA\u4E9B<strong>\u7F16\u8BD1\u7684\u5DE5\u5177</strong>\uFF0C\u4EE5\u53CA\u5982\u4F55\u4ECE\u5168\u5C40\u7684\u65B9\u5411\u5230\uFF0C</p><p>\u4E00\u4E2A\u5B50\u5305\u7684\u6253\u5305/\u7F16\u8BD1\u662F\u5982\u4F55\u8FD0\u4F5C\u7684\uFF0C\u505A\u5230\u4E00\u5957 Monorepo \u53EA\u5BF9\u5E94\u4E00\u5957\u7F16\u8BD1\u7CFB\u7EDF\u3002</p><h3 id="\u96C6\u6210\u6253\u5305\u5DE5\u5177" tabindex="-1">\u96C6\u6210\u6253\u5305\u5DE5\u5177 <a class="header-anchor" href="#\u96C6\u6210\u6253\u5305\u5DE5\u5177" aria-hidden="true">#</a></h3><p><a href="https://tuimao233.gitee.io/mao-blog/ruan-jian-kai-fa/qian-duan-bi-ji/45-rollup-javascript-mo-kuai-da-bao-qi.html" target="_blank" rel="noopener noreferrer">rollup\uFF08\u6846\u67B6/\u5E93\uFF09</a>\u3001 <a href="https://tuimao233.gitee.io/mao-blog/ruan-jian-kai-fa/qian-duan-bi-ji/23-webpack-quan-mian-fen-xi.html" target="_blank" rel="noopener noreferrer">webpack\uFF08\u5E94\u7528\u7A0B\u5E8F\uFF09</a>\u3001 <a href="https://tuimao233.gitee.io/mao-blog/ruan-jian-kai-fa/qian-duan-bi-ji/41-vite-shi-yong-yu-yuan-li-fen-xi.html#vite-%E6%98%AF%E4%BB%80%E4%B9%88" target="_blank" rel="noopener noreferrer">vite\uFF08\u5E94\u7528\u7A0B\u5E8F\uFF09</a></p><p>\u7279\u70B9\uFF1A\u751F\u6001\u3001\u63D2\u4EF6\u5B8C\u5584\u3001\u6447\u6811\u3001\u5408\u5E76\u6253\u5305</p><h3 id="rollup-\u5F00\u53D1\u6846\u67B6-\u5E93\u7684\u4F18\u52BF" tabindex="-1">Rollup \u5F00\u53D1\u6846\u67B6 / \u5E93\u7684\u4F18\u52BF <a class="header-anchor" href="#rollup-\u5F00\u53D1\u6846\u67B6-\u5E93\u7684\u4F18\u52BF" aria-hidden="true">#</a></h3><ul><li>\u914D\u7F6E\u7684\u7B80\u6613\u6027\u3001\u8BBE\u8BA1\u826F\u597D\u7684\u63D2\u4EF6\u7CFB\u7EDF</li><li>\u8F93\u51FA\u7ED3\u679C\u66F4\u52A0\u6241\u5E73\uFF0C\u6267\u884C\u6548\u7387\u66F4\u9AD8</li><li>\u81EA\u52A8\u79FB\u9664\u672A\u5F15\u7528\u7684\u4EE3\u7801</li><li>\u6253\u5305\u7ED3\u679C\u4F9D\u7136\u5B8C\u5168\u53EF\u8BFB</li></ul><h3 id="rollup-\u5F00\u53D1\u5E94\u7528\u7A0B\u5E8F\u7684\u7F3A\u70B9" tabindex="-1">Rollup \u5F00\u53D1\u5E94\u7528\u7A0B\u5E8F\u7684\u7F3A\u70B9 <a class="header-anchor" href="#rollup-\u5F00\u53D1\u5E94\u7528\u7A0B\u5E8F\u7684\u7F3A\u70B9" aria-hidden="true">#</a></h3><ul><li>\u52A0\u8F7D\u975EESM\u7684\u7B2C\u4E09\u65B9\u6A21\u5757\u6BD4\u8F83\u590D\u6742</li><li>\u6A21\u5757\u6700\u7EC8\u90FD\u4F1A\u88AB\u6253\u5305\u5230\u4E00\u4E2A\u51FD\u6570\u4E2D\uFF0C\u65E0\u6CD5\u5B9E\u73B0HMR</li><li>\u6D4F\u89C8\u5668\u73AF\u5883\u4E2D\uFF0C\u4EE3\u7801\u62C6\u5206\u529F\u80FD\u4F9D\u8D56AMD\u5E93</li></ul><h2 id="\u57FA\u7840\u7F16\u8BD1\u5DE5\u5177" tabindex="-1">\u57FA\u7840\u7F16\u8BD1\u5DE5\u5177 <a class="header-anchor" href="#\u57FA\u7840\u7F16\u8BD1\u5DE5\u5177" aria-hidden="true">#</a></h2><p><a href="https://www.typescriptlang.org/" target="_blank" rel="noopener noreferrer">tsc\uFF08js\uFF09</a>\u3001 <a href="https://esbuild.github.io/" target="_blank" rel="noopener noreferrer">esbuild\uFF08go\uFF09</a>\u3001 <a href="https://swc.rs/" target="_blank" rel="noopener noreferrer">swc\uFF08rust\uFF09</a>\u3001 <a href="https://www.babeljs.cn/" target="_blank" rel="noopener noreferrer">babel\uFF08js\uFF09</a></p><p>\u7279\u70B9\uFF1A\u5E95\u5C42\u3001\u89E3\u6790\u3001\u8F6C\u6362</p><ul><li><p><strong>ESBuild</strong> \u662F\u57FA\u4E8E Go \u8BED\u8A00\u5F00\u53D1\u7684 JavaScript Bundler, \u7531 Figma \u524D CTO <code>Evan Wallace</code> \u5F00\u53D1, \u5E76\u4E14\u4E5F\u88AB <code>Vite</code> \u7528\u4E8E\u5F00\u53D1\u73AF\u5883\u7684\u4F9D\u8D56\u89E3\u6790\u548C <code>Transform</code>.</p></li><li><p><strong>SWC</strong> \u5219\u662F\u57FA\u4E8E Rust \u7684 JavaScript Compiler(\u5176\u751F\u6001\u4E2D\u4E5F\u5305\u542B\u6253\u5305\u5DE5\u5177), \u76EE\u524D\u4E3A <code>Next.JS/Parcel/Deno</code> \u7B49\u524D\u7AEF\u5708\u77E5\u540D\u9879\u76EE\u4F7F\u7528.</p></li></ul><hr><p>\u8FD9\u4E24\u4E2A\u6784\u5EFA\u5DE5\u5177\u6709\u4EC0\u4E48\u7279\u70B9\uFF0C\u4E3A\u4EC0\u4E48\u8981\u5173\u6CE8\u4ED6\u4EEC\uFF1F\u56E0\u4E3A...</p><p><img src="https://pic3.zhimg.com/80/v2-ce79dc3bcba1a24516214fc42066cc82_1440w.jpg" alt=""></p><ul><li>\u9879\u76EE\u7684\u6784\u5EFA\u65F6\u95F4\u968F\u7740\u9879\u76EE\u4F53\u79EF\u548C\u590D\u6742\u5EA6\u9010\u6E10\u9012\u589E, \u6709\u7684\u65F6\u5019\u672C\u5730\u7F16\u8F91\u4E00\u4E2A\u9879\u76EE\u8981\u7B49\u4E0A\u4E2A\u5927\u51E0\u5206\u949F(\u6B64\u5904@Webpack)</li></ul><p><img src="https://pic4.zhimg.com/80/v2-9a173816c43709c3bcb88e950f54306b_1440w.jpg" alt=""></p><ul><li>\u8FD9\u4E2A\u662F ESBuild \u5B98\u7F51\u5BF9\u4E8E\u5176\u6253\u530510\u4EFD <code>three.js</code> \u7684\u901F\u5EA6\u5BF9\u6BD4</li></ul><p><img src="https://pic1.zhimg.com/80/v2-570a0a2e25af7e412db9ce5dc15c53b0_1440w.jpg" alt=""></p><ul><li>SWC \u5219\u5BA3\u79F0\u5176\u6BD4 Babel \u5FEB20\u500D(\u56DB\u6838\u60C5\u51B5\u4E0B\u53EF\u4EE5\u5FEB70\u500D)</li></ul><p><img src="https://pic3.zhimg.com/80/v2-4a049b1b7bfa6cff5e0b69c66f13983a_1440w.png" alt=""></p><h2 id="\u4E24\u79CD\u7C7B\u578B\u7684\u4F5C\u7528" tabindex="-1">\u4E24\u79CD\u7C7B\u578B\u7684\u4F5C\u7528 <a class="header-anchor" href="#\u4E24\u79CD\u7C7B\u578B\u7684\u4F5C\u7528" aria-hidden="true">#</a></h2><p>\u96C6\u6210\u6253\u5305\u5DE5\u5177\u662F\u53EF\u4EE5\u548C\u5176\u4ED6\u7F16\u8BD1\u5DE5\u5177\u914D\u5408\u4F7F\u7528\u7684\uFF0C\u7279\u522B\u662F\u5728 rollup \u4E2D\uFF0C</p><p>\u80FD\u5F88\u65B9\u4FBF\u7684\u5229\u7528 esbuild \u548C swc \u7684\u5E95\u5C42\u7F16\u8BD1\u4ECE\u800C\u63D0\u5347\u7F16\u8BD1\u901F\u5EA6</p><h2 id="tsc-\u74F6\u9888-tsc-bottleneck" tabindex="-1">TSC \u74F6\u9888 (TSC Bottleneck) <a class="header-anchor" href="#tsc-\u74F6\u9888-tsc-bottleneck" aria-hidden="true">#</a></h2><h3 id="deno-1-0-\u6B63\u5F0F\u53D1\u5E03\u8BF4\u660E\u4E2D\u63D0\u5230" tabindex="-1">Deno 1.0 \u6B63\u5F0F\u53D1\u5E03\u8BF4\u660E\u4E2D\u63D0\u5230 <a class="header-anchor" href="#deno-1-0-\u6B63\u5F0F\u53D1\u5E03\u8BF4\u660E\u4E2D\u63D0\u5230" aria-hidden="true">#</a></h3><blockquote><p>\u5728\u5185\u90E8\uFF0CDeno \u4F7F\u7528 Microsoft \u7684 TypeScript \u7F16\u8BD1\u5668\u68C0\u67E5\u7C7B\u578B\u5E76\u751F\u6210 JavaScript\u3002 \u4E0E V8 \u89E3\u6790 JavaScript \u6240\u82B1\u8D39\u7684\u65F6\u95F4\u76F8\u6BD4\uFF0C\u5B83\u975E\u5E38\u6162\u3002 \u5728\u9879\u76EE\u7684\u65E9\u671F\uFF0C\u6211\u4EEC\u66FE\u5E0C\u671B\u201C V8\u5FEB\u7167\u201D\u5728\u6B64\u80FD\u591F\u5E26\u6765\u91CD\u5927\u6539\u8FDB\u3002 \u5FEB\u7167\u80AF\u5B9A\u6709\u5E2E\u52A9\uFF0C\u4F46\u662F\u5B83\u4ECD\u7136\u7F13\u6162\u3002 \u6211\u4EEC\u5F53\u7136\u8BA4\u4E3A\u53EF\u4EE5\u5728\u73B0\u6709 TypeScript \u7F16\u8BD1\u5668\u7684\u57FA\u7840\u4E0A\u8FDB\u884C\u4E00\u4E9B\u6539\u8FDB\uFF0C\u4F46\u662F\u5BF9\u6211\u4EEC\u6765\u8BF4\u5F88\u663E\u7136\uFF0C\u6700\u7EC8\u9700\u8981\u5728 Rust \u4E2D\u5B9E\u73B0\u7C7B\u578B\u68C0\u67E5\u3002 \u8FD9\u5C06\u662F\u4E00\u9879\u8270\u5DE8\u7684\u4EFB\u52A1\uFF0C\u4E0D\u4F1A\u5F88\u5FEB\u53D1\u751F\u3002 \u4F46\u5B83\u53EF\u4EE5\u5728\u5F00\u53D1\u4EBA\u5458\u7ECF\u5386\u7684\u5173\u952E\u8DEF\u5F84\u4E0A\u63D0\u4F9B\u6570\u91CF\u7EA7\u7684\u6027\u80FD\u6539\u8FDB\u3002 TSC\u5FC5\u987B\u79FB\u690D\u5230Rust\u3002 \u5982\u679C\u60A8\u6709\u5174\u8DA3\u5408\u4F5C\u89E3\u51B3\u6B64\u95EE\u9898\uFF0C\u8BF7\u4E0E\u6211\u4EEC\u8054\u7CFB\u3002</p></blockquote><h3 id="deno-\u5185\u90E8\u90E8\u5206\u6A21\u5757\u505C\u7528-typescript" tabindex="-1">Deno \u5185\u90E8\u90E8\u5206\u6A21\u5757\u505C\u7528 TypeScript <a class="header-anchor" href="#deno-\u5185\u90E8\u90E8\u5206\u6A21\u5757\u505C\u7528-typescript" aria-hidden="true">#</a></h3><ul><li>\u5F53\u66F4\u6539\u6587\u4EF6\u65F6\uFF0CTypeScript \u7684\u7F16\u8BD1\u9700\u8981\u51E0\u5206\u949F\uFF0C\u8FD9\u4F7F\u5F97\u9879\u76EE\u6587\u4EF6\u7684\u8FDE\u7EED\u7F16\u8BD1\u975E\u5E38\u7F13\u6162\u3002</li><li>\u5728\u521B\u5EFA\u5B9E\u9645\u7684 Deno \u53EF\u6267\u884C\u6587\u4EF6\u548C\u9762\u5411\u7528\u6237\u7684 API \u6587\u4EF6\u65F6\uFF0C\u4F7F\u7528\u7684 TypeScript \u7ED3\u6784\u4F1A\u9020\u6210\u9879\u76EE\u8FD0\u884C\u7684\u6027\u80FD\u95EE\u9898\u3002 \u4E8B\u5B9E\u8BC1\u660E\uFF0CTypeScript \u672C\u8EAB\u5BF9 Deno \u4EE3\u7801\u7BA1\u7406\u6CA1\u6709\u5E2E\u52A9\uFF0C\u5E76\u4E14 Deno \u56E2\u961F\u6B63\u7ECF\u53D7\u7740\u76F8\u53CD\u7684\u6548\u679C\u3002\u5728\u9879\u76EE\u7684\u8BAE\u9898\u5217\u8868\u4E2D\u5C31\u63D0\u5230\u4E00\u4E2A\u95EE\u9898\uFF1A\u5728\u4E24\u4E2A\u4E0D\u540C\u7684\u4F4D\u7F6E\u4EA7\u751F\u4E86\u76F8\u540C\u7684\u72EC\u7ACB\u4E3B\u4F53\u7C7B\u3002</li><li>\u5FC5\u987B\u624B\u52A8\u4FDD\u6301\u5185\u90E8\u4EE3\u7801\u548C\u8FD0\u884C\u65F6 TypeScript \u58F0\u660E\u7684\u540C\u6B65\uFF0C\u56E0\u4E3A TypeScript \u7F16\u8BD1\u5668\u5BF9\u751F\u6210 d.ts \u6587\u4EF6\u6CA1\u6709\u5E2E\u52A9\u3002</li><li>Deno \u56E2\u961F\u9700\u8981\u53BB\u7EF4\u62A4\u4E24\u53F0 TS \u7F16\u8BD1\u5668\u4E3B\u673A\uFF1A\u4E00\u4E2A\u7528\u4E8E\u5185\u90E8\u4EE3\u7801\uFF0C\u53E6\u4E00\u4E2A\u7528\u4E8E\u5916\u90E8\u7528\u6237\u4EE3\u7801\uFF0C\u5C3D\u7BA1\u4E24\u8005\u7684\u76EE\u6807\u76F8\u4F3C\u3002</li></ul><h3 id="swc-\u56E2\u961F\u6B63\u5728\u901A\u8FC7\u79FB\u690D-tsc-\u5230-go" tabindex="-1">swc \u56E2\u961F\u6B63\u5728\u901A\u8FC7\u79FB\u690D tsc \u5230 Go <a class="header-anchor" href="#swc-\u56E2\u961F\u6B63\u5728\u901A\u8FC7\u79FB\u690D-tsc-\u5230-go" aria-hidden="true">#</a></h3><blockquote><p>\u968F\u7740 TypeScript \u7684\u91C7\u7528\u7387\u4E0D\u65AD\u63D0\u9AD8\uFF0C\u5927\u578B\u9879\u76EE\u6B63\u9762\u4E34\u4E24\u96BE\u5883\u5730\uFF1A\u7C7B\u578B\u68C0\u67E5\u662F\u5176\u5DE5\u4F5C\u6D41\u7A0B\u4E2D\u6700\u6162\u7684\u90E8\u5206\u4E4B\u4E00\u3002\u5F00\u53D1\u4EBA\u5458\u5E0C\u671B\u7C7B\u578B\u5B89\u5168\u800C\u4E0D\u9700\u8981\u4EE5\u8F83\u6162\u7684\u8FED\u4EE3\u5468\u671F\u4E3A\u4EE3\u4EF7\u3002</p></blockquote><blockquote><p>TypeScript \u7F16\u8BD1\u5668\uFF0C\u6216\u8005tsc\uFF0C\u68C0\u67E5\u4F60\u7684\u7C7B\u578B\u7684\u6709\u6548\u6027\u5E76\u5C06\u4F60\u7684\u4EE3\u7801\u7F16\u8BD1\u6210 JavaScript\u3002\u60A8\u62E5\u6709\u7684\u4EE3\u7801\u8D8A\u591A\uFF0C\u7F16\u8BD1\u6240\u9700\u7684\u65F6\u95F4\u5C31\u8D8A\u957F\u3002\u5728\u5927\u4E2D\u578B TypeScript \u9879\u76EE\u4E2D\uFF0C\u8FD9\u79CD\u7F16\u8BD1\u901F\u5EA6\u975E\u5E38\u6162\u3002\u867D\u7136\u5F00\u53D1\u4EBA\u5458\u53EF\u4EE5\u7528 SWC \u66FF\u6362\u5176\u5DE5\u4F5C\u6D41\u7A0B\u7684\u8F6C\u8BD1\u90E8\u5206\uFF0C\u4F46\u7C7B\u578B\u68C0\u67E5\u4ECD\u7136\u662F\u4E00\u4E2A\u74F6\u9888\u3002</p></blockquote><p><a href="https://kdy1.dev/posts/2022/1/tsc-go" target="_blank" rel="noopener noreferrer">https://kdy1.dev/posts/2022/1/tsc-go</a></p>',36),i=[l];function c(p,n,s,d,h,u){return a(),t("div",null,i)}var f=e(o,[["render",c]]);export{g as __pageData,f as default};
